package estrutura_repetitivas;

public class TesteMesa06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 4;
		int y = 0;
		int i = 0;
		
		while (i < x) {
			i = i + 1;
			y = y + 1;
			
			System.out.println("Valor de I = " + i);
			System.out.println("Valor de y = " + y);
		}
	}

}
